mapper.select:
select count(*)
from PROJECT


        status = #{status}


        and `name` like CONCAT("%", #{keyword}, "%")


        and status = #{status}


        and min_pay < #{minPay} and max_pay > #{minPay}


        and min_pay < #{maxPay} and max_pay > #{maxPay}


        and (min_pay < #{minPay} or max_pay > #{maxPay})


        and city in

            #{item}



        and industry in

            #{item}



        and resident = #{resident}


        and freeze = #{freeze}

select

,

from PROJECT


    status = #{status}


    and `name` like CONCAT("%", #{keyword}, "%")


    and status = #{status}


    and min_pay < #{minPay} and max_pay > #{minPay}


    and min_pay < #{maxPay} and max_pay > #{maxPay}


    and (min_pay < #{minPay} or max_pay > #{maxPay})


    and city in

        #{item}



    and industry in

        #{item}



    and resident = #{resident}


    and freeze = #{freeze}


order by update_time desc
limit #{offset}, #{pageSize}
